/**
 * User Settings Page Module
 */
var UserSettings = \(function\(\) {
    var container;
    var users = [];

    function init\(el\) {
        container = el;
        loadUsers\(\);
    }

    function loadUsers\(\) {
        console.log\('[UserSettings] Loading users...'\);

        Auth.getUsers\(\)
            .then\(function\(data\) {
                console.log\('[UserSettings] Users loaded:', data\);
                // Handle different response formats
                if \(!data\) {
                    data = {};
                }
                // Check if data itself is the users array or contains a users property
                if \(Array.isArray\(data\)\) {
                    users = data;
                } else if \(Array.isArray\(data.users\)\) {
                    users = data.users;
                } else {
                    users = [];
                }

                if \(!Array.isArray\(users\)\) {
                    console.error\('[UserSettings] Users is not an array:', users\);
                    users = [];
                }

                // Filter out invalid user objects
                users = users.filter\(function\(user\) {
                    return user && typeof user === 'object' && \(user.username || user.id\);
                }\);

                render\(\);
            }\)
            .catch\(function\(err\) {
                console.error\('[UserSettings] Failed to load users:', err\);
                container.innerHTML = '<div class="error">Failed to load users: ' + err.message + '</div>';
            }\);
    }

    function render\(\) {
        console.log\('[UserSettings] Rendering users, count:', users.length\);

        var htmlParts = [];
        htmlParts.push\('<div class="toolbar">'\);
        htmlParts.push\('  <div class="search-box">'\);
        htmlParts.push\('    <input type="text" id="userSearch" placeholder="Search users..." autocomplete="off">'\);
        htmlParts.push\('  </div>'\);
        htmlParts.push\('  <button class="btn-primary" id="addUserBtn">Add User</button>'\);
        htmlParts.push\('</div>'\);
        htmlParts.push\('<table class="data-table">'\);
        htmlParts.push\('  <thead>'\);
        htmlParts.push\('    <tr>'\);
        htmlParts.push\('      <th>ID</th>'\);
        htmlParts.push\('      <th>Username</th>'\);
        htmlParts.push\('      <th>Role</th>'\);
        htmlParts.push\('      <th>Created At</th>'\);
        htmlParts.push\('      <th>Actions</th>'\);
        htmlParts.push\('    </tr>'\);
        htmlParts.push\('  </thead>'\);
        htmlParts.push\('  <tbody id="userList"></tbody>'\);
        htmlParts.push\('</table>'\);
        htmlParts.push\('<div id="userModal"></div>'\);

        container.innerHTML = htmlParts.join\(''\);

        var tbody = document.getElementById\('userList'\);
        if \(!users || users.length === 0\) {
            tbody.innerHTML = '<tr><td colspan="5" class="empty-state">No users found</td></tr>';
        } else {
            tbody.innerHTML = users.map\(function\(user\) {
                var badgeClass = 'badge-success';
                if \(user.role === 'owner'\) badgeClass = 'badge-danger';
                else if \(user.role === 'admin'\) badgeClass = 'badge-info';
                return '<tr>' +
                    '<td>' + \(user.id || ''\) + '</td>' +
                    '<td>' + escapeHtml\(user.username\) + '</td>' +
                    '<td><span class="badge ' + badgeClass + '">' + escapeHtml\(user.role\) + '</span></td>' +
                    '<td>' + formatDate\(user.createdAt\) + '</td>' +
                    '<td class="actions">' +
                    '  <button class="btn-edit" data-id="' + user.id + '">Edit</button>' +
                    '  <button class="btn-delete" data-id="' + user.id + '">Delete</button>' +
                    '</td>' +
                    '</tr>';
            }\).join\(''\);
        }

        setupEventListeners\(\);
    }

    function setupEventListeners\(\) {
        var searchInput = document.getElementById\('userSearch'\);
        if \(searchInput\) {
            searchInput.addEventListener\('input', function\(e\) {
                filterUsers\(e.target.value\);
            }\);
        }

        container.addEventListener\('click', function\(e\) {
            var target = e.target;
            if \(target.classList.contains\('btn-edit'\)\) {
                var id = parseInt\(target.getAttribute\('data-id'\)\);
                var user = users.find\(function\(u\) { return u.id === id; }\);
                if \(user\) {
                    showUserModal\(user\);
                }
            } else if \(target.classList.contains\('btn-delete'\)\) {
                var id = parseInt\(target.getAttribute\('data-id'\)\);
                if \(confirm\('Are you sure you want to delete this user?'\)\) {
                    deleteUser\(id\);
                }
            }
        }\);
    }

    function filterUsers\(query\) {
        if \(!users || !Array.isArray\(users\)\) {
            return;
        }

        var filtered = users.filter\(function\(user\) {
            var username = user.username || '';
            var role = user.role || '';
            return username.toLowerCase\(\).indexOf\(query.toLowerCase\(\)\) !== -1 ||
                role.toLowerCase\(\).indexOf\(query.toLowerCase\(\)\) !== -1;
        }\);

        var tbody = document.getElementById\('userList'\);
        if \(filtered.length === 0\) {
            tbody.innerHTML = '<tr><td colspan="5" class="empty-state">No matching users</td></tr>';
        } else {
            tbody.innerHTML = filtered.map\(function\(user\) {
                var badgeClass = 'badge-success';
                if \(user.role === 'owner'\) badgeClass = 'badge-danger';
                else if \(user.role === 'admin'\) badgeClass = 'badge-info';
                return '<tr>' +
                    '<td>' + \(user.id || ''\) + '</td>' +
                    '<td>' + escapeHtml\(user.username\) + '</td>' +
                    '<td><span class="badge ' + badgeClass + '">' + escapeHtml\(user.role\) + '</span></td>' +
                    '<td>' + formatDate\(user.createdAt\) + '</td>' +
                    '<td class="actions">' +
                    '  <button class="btn-edit" data-id="' + user.id + '">Edit</button>' +
                    '  <button class="btn-delete" data-id="' + user.id + '">Delete</button>' +
                    '</td>' +
                    '</tr>';
            }\).join\(''\);
        }
    }

    function showUserModal\(user\) {
        var isEdit = !!user;
        var modalHtmlParts = [];
        modalHtmlParts.push\('<div class="modal-overlay">'\);
        modalHtmlParts.push\('  <div class="modal">'\);
        modalHtmlParts.push\('    <h2>' + \(isEdit ? 'Edit User' : 'Add User'\) + '</h2>'\);
        modalHtmlParts.push\('    <div id="userFormMessage"></div>'\);
        modalHtmlParts.push\('    <form id="userForm">'\);
        modalHtmlParts.push\('      <input type="hidden" name="id" value="' + \(user ? user.id : ''\) + '">'\);
        modalHtmlParts.push\('      <div class="form-group">'\);
        modalHtmlParts.push\('        <label for="username">Username</label>'\);
        modalHtmlParts.push\('        <input type="text" id="username" name="username" value="' + \(user ? escapeHtml\(user.username\) : ''\) + '" required minlength="3">'\);
        modalHtmlParts.push\('      </div>'\);
        modalHtmlParts.push\('      <div class="form-group">'\);
        modalHtmlParts.push\('        <label for="password">Password' + \(isEdit ? ' \(leave blank to keep current\)' : ''\) + '</label>'\);
        modalHtmlParts.push\('        <input type="password" id="password" name="password" ' + \(isEdit ? '' : 'required'\) + ' minlength="6">'\);
        modalHtmlParts.push\('      </div>'\);
        modalHtmlParts.push\('      <div class="form-group">'\);
        modalHtmlParts.push\('        <label for="role">Role</label>'\);
        modalHtmlParts.push\('        <select id="role" name="role">'\);
        modalHtmlParts.push\('          <option value="member"' + \(user && user.role === 'member' ? ' selected' : ''\) + '>Member</option>'\);
        modalHtmlParts.push\('          <option value="admin"' + \(user && user.role === 'admin' ? ' selected' : ''\) + '>Admin</option>'\);
        modalHtmlParts.push\('        </select>'\);
        modalHtmlParts.push\('      </div>'\);
        modalHtmlParts.push\('      <div class="modal-footer">'\);
        modalHtmlParts.push\('        <button type="button" class="btn-cancel" id="cancelUserBtn">Cancel</button>'\);
        modalHtmlParts.push\('        <button type="submit" class="btn-primary">' + \(isEdit ? 'Update' : 'Create'\) + '</button>'\);
        modalHtmlParts.push\('      </div>'\);
        modalHtmlParts.push\('    </form>'\);
        modalHtmlParts.push\('  </div>'\);
        modalHtmlParts.push\('</div>'\);

        var modalContainer = document.getElementById\('userModal'\);
        modalContainer.innerHTML = modalHtmlParts.join\(''\);

        var form = document.getElementById\('userForm'\);
        var messageDiv = document.getElementById\('userFormMessage'\);

        document.getElementById\('cancelUserBtn'\).addEventListener\('click', function\(\) {
            modalContainer.innerHTML = '';
        }\);

        form.addEventListener\('submit', function\(e\) {
            e.preventDefault\(\);

            var formData = {
                username: document.getElementById\('username'\).value,
                role: document.getElementById\('role'\).value
            };

            var password = document.getElementById\('password'\).value;
            if \(password\) {
                formData.password = password;
            }

            var promise;
            if \(isEdit\) {
                promise = Auth.updateUser\(user.id, formData\);
            } else {
                promise = Auth.createUser\(formData.username, password || formData.password, formData.role\);
            }

            promise
                .then\(function\(\) {
                    modalContainer.innerHTML = '';
                    loadUsers\(\);
                }\)
                .catch\(function\(err\) {
                    messageDiv.innerHTML = '<div class="error">' + err.message + '</div>';
                }\);
        }\);
    }

    function deleteUser\(id\) {
        Auth.deleteUser\(id\)
            .then\(function\(\) {
                loadUsers\(\);
            }\)
            .catch\(function\(err\) {
                alert\('Failed to delete user: ' + err.message\);
            }\);
    }

    function escapeHtml\(text\) {
        if \(!text\) return '';
        var div = document.createElement\('div'\);
        div.textContent = text;
        return div.innerHTML;
    }

    function formatDate\(dateStr\) {
        if \(!dateStr\) return '-';
        try {
            var date = new Date\(dateStr\);
            return date.toLocaleDateString\(\) + ' ' + date.toLocaleTimeString\(\);
        } catch \(e\) {
            return dateStr;
        }
    }

    return {
        init: init
    };
}\)\(\);
